INSERT INTO `dede_feedback` (`id`, `aid`, `typeid`, `username`, `arctitle`, `ip`, `ischeck`, `dtime`, `mid`, `bad`, `good`, `ftype`, `face`, `msg`) VALUES
(17, 167, 1, '哎哟哥哥', '比尔盖茨：谷歌别光演戏，干点儿实在的', '127.0.0.1', 1, 1376031268, 5, 0, 0, 'feedback', 1, 'google是以互联网搜索引擎为出身的新贵公司，微软则是老牌软件公司，在属性上有本质不同，google认为科技发展到一定阶段可以质变，即所谓的要用气球满足无法使用互联网的人，即使那些人身患重病，他们仍需要科技，而如今的没落贵族微软则更加现实些，或许这也是google和微软能走到今天这地步的不同原因吧！。。'),
(16, 169, 1, '韦小宝', '齐齐封杀令背后：阿里与腾讯的“社交电商”博弈', '127.0.0.1', 1, 1376031024, 8, 0, 0, 'feedback', 1, '好图！二马的表情戏好赞！左边的妹子对剧本也吃得很透！！！'),
(15, 169, 1, '令狐冲', '齐齐封杀令背后：阿里与腾讯的“社交电商”博弈', '127.0.0.1', 1, 1376030938, 6, 0, 2, 'feedback', 1, '阿里本来就是个生态系统，从这个角度说，从外部导入流量并没有错，无论美丽说、蘑菇街，或者微信都可以。一家独大对阿里系未必有好处，试图垄断的企业，大多走向没落的趋势，大如微软也是如此，正在为走出困境而努力。回想下阿里系的成功，正是在搭建平台上，承载了大小卖家、物流企业、金融企业。适度开放，研究下开放策略比一刀切打死平台更好！'),
(18, 136, 6, '站酷源码', '马云退，陆兆禧走上前台', '127.0.0.1', 1, 1376031723, 1, 0, 0, 'feedback', 1, '光环与压力同在，马云的班真的不好接'),
(23, 169, 0, '站酷源码', '齐齐封杀令背后：阿里与腾讯的“社交电商”博弈', '127.0.0.1', 1, 1376045238, 1, 0, 0, 'feedback', 0, '微信借着自己拿着现在在移动端最稳的半张船票，就想通过5.0把整个互联网的需求涵盖了，这也太一厢情愿了，场子铺那么大拉的来客么~~什么微信不是营销工具，微信5.0公众平台付费版的，比现在公众平台的营销性强太多了，微信这产品真的是太急了，生态哪有这么好弄得~~'),
(24, 169, 1, '路人甲', '齐齐封杀令背后：阿里与腾讯的“社交电商”博弈', '127.0.0.1', 1, 1376048469, 9, 0, 0, 'feedback', 1, '未来人们的互联网中心移到手机上之后，我更期待的是移动的社交化电商和更本地化生活化的电商，不知道微信上电商未来会发展成什么样，不过应该会会比较大的改变淘宝的模式。不论，谁抓住了未来微信上社交化电商的机会，只要能给人们带来更多的购物方便和更好的用户体验，就是英雄。。。。'),
(26, 130, 6, '站酷源码', '奥巴马与马克·扎克伯格在Facebook总部会谈', '127.0.0.1', 1, 1376127941, 1, 0, 0, 'feedback', 1, '还是这样的好啊 自由不拘谨'),
(27, 130, 6, '韦小宝', '奥巴马与马克·扎克伯格在Facebook总部会谈', '127.0.0.1', 1, 1376128469, 8, 0, 0, 'feedback', 1, '这像国家领导开会的样子吗？都没服务员小姐端茶没大红旗大标语做背景，哈哈'),
(28, 161, 1, '站酷源码', 'PC游戏转手游，实战案例分析', '127.0.0.1', 1, 1376188538, 1, 0, 0, 'feedback', 1, '好像 好玩123 是墨麟的子公司，小道消息不清楚了也不重要。<br />\n总的来说，不管是端游还是页游厂商来做手游，只要项目负责人够好，团队够拼，上层只要负责给资源支持，比小团队还是有优势的。<br />\n手游现在不管大公司还是小公司都是独立拉出来的团队，只需要投入几百万试水，所以上层是会给负责人很大的独立性的。基本上这个项目的成败就是看负责人的思维（选材、开发流程、推广方式这几点），还有团队实力（够经验够快）。'),
(29, 168, 1, '站酷源码', '腾讯开放两年都做了啥？看看腾讯的自我表扬贴', '127.0.0.1', 1, 1376188715, 1, 0, 0, 'feedback', 1, '比较佩服腾讯的一点，产品真心不错，虽说只是微创新'),
(32, 168, 1, '令狐冲', '腾讯开放两年都做了啥？看看腾讯的自我表扬贴', '127.0.0.1', 1, 1376216507, 6, 0, 0, 'feedback', 1, '公司大了，争议就不会断，国外微软如此，国内BAT同样备受争议，即使马云也被赶下神坛。不过，腾讯做开放平台，还是让不少开发者实现了创业梦想的，游戏被批评很正常，开放平台这事还是值得称赞的，中肯的来说，至少是实实在在做了些事情的。'),
(33, 164, 1, '令狐冲', '周鸿祎眼中互联网公司衰落的六大原因', '127.0.0.1', 1, 1376216745, 6, 0, 0, 'feedback', 1, '你可以否认老周的人品，你可以否认360的任何产品，你可以卸载360所有的东西，但你不能否认老周说的80%有道理。');
